info_title=#{infoTitle},
info_content = #{infoContent}, info_update=SYSDATE,
info_update_uno=#{uno}
where info_bno=#{infoBno} and uno=#{uno}

update info_board set info_blindstate = 3,
info_update=SYSDATE, info_update_uno = #{uno}
where
info_bno=#{infoBno}
and uno=#{uno}

update info_board set
